import json


def get_tiku():
    with open("tiku.txt",encoding='utf_8_sig') as f:
        result = json.load(f)
        list_tiku = result['data']['list']
        return list_tiku

def search_loacl(timu_id):
    tiku = get_tiku()
    for i in tiku:
        if i['timu_id'] == timu_id:
            # result = {"答案":i['timu_daan'],"题目":i['timu_title']}
            result = f"答案：{i['timu_daan']}，题目：{i['timu_title']}"
            return result
